import React, { useState } from 'react'
import classes from './AddUser.module.css';
import Button from '../UI/Button';
import Card from '../UI/Card';
import ErrorModal from '../UI/ErrorModal';

const AddUser = (props) => {
    const [enteredUserName, setEnteredUserName] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [error, setError] = useState();

    const addUserHandler = (e) => {
        e.preventDefault();
        if (enteredUserName.trim().length === 0 || enteredAge.trim().length === 0) {
            setError({
                title: 'Invalid input',
                message: 'Please enter a valid name and age (non-empty values).'
            })
            return;
        }
        if (+enteredAge < 1) {
            setError({
                title: 'Invalid age',
                message: 'Please enter a valid age (> 0).'
            });
            return;
        }
        props.onAdduser(enteredUserName, enteredAge);
    }

    const usernameChangedHandler = (e) => {
        setEnteredUserName(e.target.value);
    }

    const ageChangedHandler = (e) => {
        setEnteredAge(e.target.value);
    }

    const errorHandler = () => {
        setError(null);
    }

    return (
        <div>
            {error && <ErrorModal titlle={error.title} message={error.message} onConfirm={errorHandler}/>}
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor='username'>Username</label>
                    <input id='username' type='text' value={enteredUserName} onChange={usernameChangedHandler} />
                    <label htmlFor='age'>Age (Year)</label>
                    <input id='age' type='number' value={enteredAge} onChange={ageChangedHandler} />
                    <Button type='submit'>Add User</Button>
                </form>
            </Card>
        </div>

    )
}

export default AddUser
